        <script>
            $('#deleteModal').on('show.bs.modal', function (event) {
                var button = $(event.relatedTarget) 
                var prof_id = button.data('profid') 
                
                var modal = $(this)
                modal.find('.modal-footer #proff_id').val(prof_id);
                modal.find('form').attr('action','/profesiones/profesion/' + prof_id);
                
            })
        </script>
